public class ArithmeticDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 1+2;
		System.out.println(result);
		int a = 3;
		System.out.println(0%a);
		System.out.println(1%a);
		System.out.println(2%a);
	}

}
